DROP TABLE IF EXISTS `book`;
CREATE TABLE `book` (
  `cid` int NOT NULL,
  `bname` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL,
  `novel` varchar(255) DEFAULT NULL,
  `chapterNumber` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL,
  `content` text,
  `nid` int DEFAULT NULL,
  `id` int DEFAULT NULL,
  `writer` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`cid`),
  KEY `b_nid` (`nid`),
