-- ----------------------------
-- Table structure for novel
-- ----------------------------
DROP TABLE IF EXISTS `novel`;
CREATE TABLE `novel` (
  `nid` int NOT NULL AUTO_INCREMENT,
  `novel` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL,
  `writer` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL,
  `synopsis` varchar(250) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL,
  `motto` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL,
  `img` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL,
  `cid` int DEFAULT NULL,
  `cname` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL,
  `css` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL,
  `chapter` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL,
  `brief` text CHARACTER SET utf8 COLLATE utf8_general_ci,
  `list` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`nid`) USING BTREE,
  KEY `n_c_id` (`cid`) USING BTREE,
  CONSTRAINT `n_c_id` FOREIGN KEY (`cid`) REFERENCES `category` (`cid`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE=InnoDB AUTO_INCREMENT=35 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

-- ----------------------------
-- Records of novel
-- ----------------------------
INSERT INTO `novel` VALUES ('1', '元尊', '天蚕土豆', '天地为炉，万物为铜，阴阳为炭，造化为工。气运为争，蟒雀吞龙。究竟是蟒确为尊，还是圣龙崛起，凌驾众生？这是气掌乾坤的世界，磅礴宏伟，一气可搬山，可倒海，可翻天，可掌阴阳乾坤。', '心潮澎湃，无限幻想，迎风挥击千层浪', 'images/info-img-1.jpg', '1', '玄幻', 'top1', '第五章 齐岳、柳溪', '<p>\r\n                                　　天地为炉，万物为铜，阴阳为炭，造化为工。\r\n                                <br>\r\n                                　　气运为争，蟒雀吞龙。\r\n                                <br>\r\n                                　　究竟是蟒雀为尊，还是圣龙崛起，凌驾众生？\r\n                                <br>\r\n                                　　这是气掌乾坤的世界，磅礴宏伟，一气可搬山，可倒海，可翻天，可掌阴阳乾坤。\r\n                                <br>\r\n                                　　世间源气分九品，三品称玄，六品成天，九品号圣。\r\n                                <br>\r\n                                　　吾有一口玄黄气，可吞天地日月星。\r\n                                <br>\r\n                                \r\n                            </p>', '300万 字');
INSERT INTO `novel` VALUES ('2', '诡秘之主', '爱潜水的鱼', '蒸汽与机械的浪潮中，谁能触及非凡？历史和黑暗的迷雾里，又是谁在耳语？我从诡秘中醒来，睁眼看见这个世界：\r\n　　枪械，大炮，巨舰，飞空艇，差分机；魔药，占卜，诅咒，倒吊人，封印物……光明依旧照耀，神秘从未远离，这是一段“愚者”的传说。', '心潮澎湃，无限幻想，迎风挥击千层浪，少年不败热血！', 'images/info-img-2.jpg', '1', '玄幻', 'top2', '第三章 梅丽莎', '<p>\r\n                                　　蒸汽与机械的浪潮中，谁能触及非凡？历史和黑暗的迷雾里，又是谁在耳语？我从诡秘中醒来，睁眼看见这个世界:\r\n                                <br>\r\n                                　　枪械，大炮，巨舰，飞空艇，差分机；魔药，占卜，诅咒，倒吊人，封印物……光明依旧照耀，神秘从未远离，这是一段“愚者”的传说。\r\n                                </p>', '246.5万 字');
INSERT INTO `novel` VALUES ('3', '万族之劫', '老鹰吃小鸡', '我是这诸天万族的劫！', '重生过去、畅想未来、梦幻现实，再塑传奇人生！', 'images/info-img-3.jpg', '1', '都市', 'top3', '第二章 四大学府体系', '<p>\r\n                                　　我是这诸天万族的劫！\r\n                                </p>\r\n', '200.6万 字');
INSERT INTO `novel` VALUES ('4', '数风流人物', '瑞根', '瑞根晚明+红楼半架空历史官场养成文，绝对够味！大周永隆二年。盛世隐忧。四王八公鲜花着锦，文臣武将烈火烹油。内有南北文武党争不休，外有九边海疆虏寇虎视。这是一个最好的时代，也是一个最坏的时代。关键在于你身处其中时，该如何把握。勇猛精进，志愿无倦，且看我如何定风流，挽天倾！', '醒掌天下权，醉卧美人膝，五千年风华烟雨，是非成败转头空！', 'images/info-img-4.jpg', '2', '历史', 'top1', '楔子', '<p>\r\n                                　　瑞根晚明+红楼半架空历史官场养成文，绝对够味！\r\n                                <br>\r\n                                　　大周永隆二年。盛世隐忧。\r\n                                <br>\r\n                                　　四王八公鲜花着锦，文臣武将烈火烹油。\r\n                                <br>\r\n                                　　内有南北文武党争不休，外有九边海疆虏寇虎视。\r\n                                <br>\r\n                                　　这是一个最好的时代，也是一个最坏的时代。\r\n                                <br>\r\n                                　　关键在于你身处其中时，该如何把握。\r\n                                <br>\r\n                                　　勇猛精进，志愿无倦，且看我如何定风流，挽天倾！\r\n                            </p>', '11072 月票');
INSERT INTO `novel` VALUES ('5', '从火影开始掌控世界', '夜南听风', '穿越到火影世界，成为卡卡西的兄长，枫夜发现自己的脑海中多了一个虚幻时钟刻刻帝。十二个时刻。十二种不同的时间系能力！“火影世界的忍术总计八类，分别是风雷水火土、阴阳遁术、以及包含飞雷神、通灵术在内的空间忍术……”“而我则掌握着第九类忍术——时间！”', '热血的少年，为打破次元壁一往无前！', 'images/info-img-5.jpg', '2', '轻小说', 'top2', '第一章 受欢迎也是苦恼', '<p>\r\n                                　　穿越到火影世界，成为卡卡西的兄长，枫夜发现自己的脑海中多了一个虚幻时钟刻刻帝。\r\n                                <br>\r\n                                　　十二个时刻。\r\n                                <br>\r\n                                　　十二种不同的时间系能力！\r\n                                <br>\r\n                                　　“火影世界的忍术总计八类，分别是风雷水火土、阴阳遁术、以及包含飞雷神、通灵术在内的空间忍术……”\r\n                                <br>\r\n                                　　“而我则掌握着第九类忍术——时间！”      　　                               </p>', '9972 月票');
INSERT INTO `novel` VALUES ('6', '库洛牌的魔法使', '愿心不变', '有这样一个隐藏在世界沉睡的夜晚、赋予人们各种能力的系统争端。“恭喜您，获得SSS级能力。”系统的声音异常美妙。只是方然...猛地把手里的魔导书往地上狠狠一摔！破口大骂！“我去你妹的库洛牌！老子才不是魔法少女！”“说好的修仙炼体、古武斗气！炼金巫术、魔法异能呢！？”“神他喵无限流酷炫开局，然后获得魔法少女之力啊！”', '热血的少年，为打破次元壁一往无前！', 'images/info-img-6.jpg', '2', '轻小说', 'top3', '第一章 命运是平凡的……呸！', '<p>\r\n                                　　有这样一个隐藏在世界沉睡的夜晚、赋予人们各种能力的系统争端。\r\n                                <br>\r\n                                　　“恭喜您，获得SSS级能力。”\r\n                                <br>\r\n                                　　系统的声音异常美妙。\r\n                                <br>\r\n                                　　只是方然...\r\n                                <br>\r\n                                　　猛地把手里的魔导书往地上狠狠一摔！破口大骂！\r\n                                <br>\r\n                                　　“我去你妹的库洛牌！老子才不是魔法少女！”\r\n                                <br>\r\n                                　　“说好的修仙炼体、古武斗气！炼金巫术、魔法异能呢！？”\r\n                            </p>', '6852 月票');
INSERT INTO `novel` VALUES ('7', '圣墟', '辰东', '在破败中崛起，在寂灭中复苏。沧海成尘，雷电枯竭，那一缕幽雾又一次临近大地，世间的枷锁被打开了，一个全新的世界就此揭开神秘的一角……', '在破败中崛起，在寂灭中复苏。沧海成尘，雷电枯竭……', 'images/info-img-7.jpg', '3', '玄幻', 'top1', '第一章 沙漠中的彼岸花', '<p>\r\n                                　　在破败中崛起，在寂灭中复苏。\r\n                                <br>\r\n                                　　沧海成尘，雷电枯竭，那一缕幽雾又一次临近大地，世间的枷锁被打开了，一个全新的世界就此揭开神秘的一角……\r\n</p>', '5950565 收藏数');
INSERT INTO `novel` VALUES ('8', '一念永恒', '耳根', '一念成沧海，一念化桑田。一念斩千魔，一念诛万仙。唯我念……永恒。这是耳根继《仙逆》《求魔》《我欲封天》后，创作的第四部长篇小说《一念永恒》', '悠悠万载一念间，斩千魔，诛万仙，唯我永恒。', 'images/info-img-8.jpg', '3', '仙侠', 'top2', '第一章 他叫白小纯', '<p>\r\n                                　　一念成沧海，一念化桑田。一念斩千魔，一念诛万仙。\r\n                                <br>\r\n                                　　唯我念……永恒。\r\n                                <br>\r\n                                　　这是耳根继《仙逆》《求魔》《我欲封天》后，创作的第四部长篇小说《一念永恒》\r\n</p>', '5654565 收藏数');
INSERT INTO `novel` VALUES ('9', '牧神记', '宅猪', '大墟的祖训说，天黑，别出门。大墟残老村的老弱病残们从江边捡到了一个婴儿，取名秦牧，含辛茹苦将他养大。这一天夜幕降临，黑暗笼罩大墟，秦牧走出了家门……做个春风中荡漾的反派吧！瞎子对他说。秦牧的反派之路，正在崛起！', '心潮澎湃，无限幻想，迎风挥击千层浪，少年不败热血！', 'images/info-img-9.jpg', '3', '玄幻', 'top3', '第一章 天黑别出门', '<p>\r\n                                　　大墟的祖训说，天黑，别出门。\r\n                                <br>\r\n                                　　大墟残老村的老弱病残们从江边捡到了一个婴儿，取名秦牧，含辛茹苦将他养大。这一天夜幕降临，黑暗笼罩大墟，秦牧走出了家门……\r\n                                <br>\r\n                                　　做个春风中荡漾的反派吧！\r\n                                <br>\r\n                                　　瞎子对他说。\r\n                                <br>\r\n                                　　秦牧的反派之路，正在崛起！\r\n                                <br>\r\n                                \r\n                                <br>\r\n                                \r\n                            </p>', '4850565 收藏数');
INSERT INTO `novel` VALUES ('10', '伏天氏', '净无痕', '东方神州，有人皇立道统，有圣贤宗门传道，有诸侯雄踞一方王国，诸强林立，神州动乱千万载，值此之时，一代天骄叶青帝及东凰大帝横空出世，东方神州一统！然，叶青帝忽然暴毙，世间雕像尽皆被毁，于世间除名，沦为禁忌；从此神州唯东凰大帝独尊！十五年后，东海青州城，一名为叶伏天的少年，开启了他的传奇之路…', '心潮澎湃，无限幻想，迎风挥击千层浪，少年不败热血！\r\n\r\n', 'images/info-img-10.jpg', '4', '玄幻', 'top1', '第一章 此间少年', '<p>\r\n                                　　东方神州，有人皇立道统，有圣贤宗门传道，有诸侯雄踞一方王国，诸强林立，神州动乱千万载，值此之时，一代天骄叶青帝及东凰大帝横空出世，东方神州一统！\r\n                                <br>\r\n                                　　然，叶青帝忽然暴毙，世间雕像尽皆被毁，于世间除名，沦为禁忌；从此神州唯东凰大帝独尊！\r\n                                <br>\r\n                                　　十五年后，东海青州城，一名为叶伏天的少年，开启了他的传奇之路…</p>', '287262 推荐票');
INSERT INTO `novel` VALUES ('11', '斗破苍穹', '天蚕土豆', '这里是属于斗气的世界，没有花俏艳丽的魔法，有的，仅仅是繁衍到巅峰的斗气！新书等级制度：斗者，斗师，大斗师，斗灵，斗王，斗皇，斗宗，斗尊，斗圣，斗帝。\r\n……', '心潮澎湃，无限幻想，迎风挥击千层浪，少年不败热血！', 'images/info-img-11.jpg', '4', '玄幻', 'top2', '第一章 陨落的天才', '<p>\r\n                                　　这里是属于斗气的世界，没有花俏艳丽的魔法，有的，仅仅是繁衍到巅峰的斗气！\r\n                                <br>\r\n                                　　新书等级制度：斗者，斗师，大斗师，斗灵，斗王，斗皇，斗宗，斗尊，斗圣，斗帝。\r\n                                <br>\r\n                                　　……</p>', '156462 推荐票');
INSERT INTO `novel` VALUES ('12', '斗罗大陆Ⅳ终极斗罗', '唐家三少', '一万年后，冰化了。斗罗联邦科考队在极北之地科考时发现了一个有着金银双色花纹的蛋，用仪器探察之后，发现里面居然有生命体征，赶忙将其带回研究所进行孵化。蛋孵化出来了，可孵出来的却是一个婴儿，和人类一模一样的婴儿，一个蛋生的孩子。', '心潮澎湃，无限幻想，迎风挥击千层浪，少年不败热血！', 'images/info-img-12.jpg', '4', '玄幻', 'top3', '第一章 那是什么？', '<p>\r\n                            \r\n                                　　一万年后，冰化了。<br>　　斗罗联邦科考队在极北之地科考时发现了一个有着金银双色花纹的蛋，用仪器探察之后，发现里面居然有生命体征，赶忙将其带回研究所进行孵化。蛋孵化出来了，可孵出来的却是一个婴儿，和人类一模一样的婴儿，一个蛋生的孩子。\r\n                            \r\n                        </p>', '123253 推荐票');
INSERT INTO `novel` VALUES ('13', '垂钓之神', '会狼叫的猪', '这个星球被海洋覆盖。人类悬空而居，每当少年礼时，所有的孩子将进行垂钓测试，根骨奇佳者，有可能成为伟大的钓师。在无尽海域。每一种生命都被赋予神圣的使命，这里有飞天遁地之鱼，有受尽天地精华之龟，有口吞天地之鲸……还有无数垂钓之人。垂钓，是一门技术。这里流传着一句古话，如果你不是在垂钓，就是在去垂钓的路上。', '心潮澎湃，无限幻想，迎风挥击千层浪，少年不败热血！', 'images/info-img-13.jpg', '5', '玄幻', 'top1', '第一章 被拖进海底的少年', '<p>\r\n                                　　这个星球被海洋覆盖。\r\n                                <br>\r\n                                　　人类悬空而居，每当少年礼时，所有的孩子将进行垂钓测试，根骨奇佳者，有可能成为伟大的钓师。\r\n                                <br>\r\n                                　　在无尽海域。\r\n                                <br>\r\n                                　　每一种生命都被赋予神圣的使命，这里有飞天遁地之鱼，有受尽天地精华之龟，有口吞天地之鲸……还有无数垂钓之人。\r\n                                <br>\r\n                                　　垂钓，是一门技术。\r\n                                <br>\r\n                                　　这里流传着一句古话，如果你不是在垂钓，就是在去垂钓的路上。    </p>', '5057602');
INSERT INTO `novel` VALUES ('14', '随身带个狩猎空间', '青空洗雨', '陆泽穿越到了两千年后的星际时代。前身自带常年秀恩爱虐狗的父母和可爱的妹妹，虽然修炼天赋普普通通，但是生活圆满，可以说是梦幻开局了，陆泽表示很满意。结果刚一睡下，陆泽就来到了个奇怪的空间。在差点和一只一米高的超级大白兔打个一换一，艰难完成击杀之后，陆泽发现，事情好像不是那么简单。在空间中击杀猎物之后获得的小光团竟然能用来修炼？这不是要让我成为万年打野么？不过，纵横星空，无双于世，貌似可以期待一下？', '心潮澎湃，无限幻想，迎风挥击千层浪，少年不败热血！', 'images/info-img-14.jpg', '5', '玄幻', 'top2', '第一章 去掉头应该能吃？', '<p>\r\n                                　　陆泽穿越到了两千年后的星际时代。\r\n                                <br>\r\n                                　　前身自带常年秀恩爱虐狗的父母和可爱的妹妹，虽然修炼天赋普普通通，但是生活圆满，可以说是梦幻开局了，陆泽表示很满意。\r\n                                <br>\r\n                                　　结果刚一睡下，陆泽就来到了个奇怪的空间。\r\n                                <br>\r\n                                　　在差点和一只一米高的超级大白兔打个一换一，艰难完成击杀之后，陆泽发现，事情好像不是那么简单。\r\n                                <br>\r\n                                　　在空间中击杀猎物之后获得的小光团竟然能用来修炼？\r\n                                <br>\r\n                                　　这不是要让我成为万年打野么？\r\n                                <br>\r\n                                　　不过，纵横星空，无双于世，貌似可以期待一下？\r\n                            </p>', '4856654');
INSERT INTO `novel` VALUES ('15', '猎妖高校', '郑重骑士', '高考结束后，郑清没有去心仪已久的帝都大学，而是背着行李，抱着狐狸，循着一纸通知，闯进一座陌生的高校。这里没有高数、英语、思想政治，取而代之的是占卜、天文、魔法的哲学。这里的课外活动是狩猎妖魔，这里的兴趣爱好千奇百怪。泛舟学海，攀登书山。这个一脸茫然的年轻人跌跌撞撞的开始了自己的巫师生涯。', '魔兽践踏，巨龙咆哮，巫师诅咒，魔法璀璨之光照耀知识灯塔！', 'images/info-img-15.jpg', '5', '奇幻', 'top3', '\r\n第一章 莫名其妙的考试', '<p>\r\n                                　　高考结束后，郑清没有去心仪已久的帝都大学，而是背着行李，抱着狐狸，循着一纸通知，闯进一座陌生的高校。\r\n                                <br>\r\n                                　　这里没有高数、英语、思想政治，取而代之的是占卜、天文、魔法的哲学。\r\n                                <br>\r\n                                　　这里的课外活动是狩猎妖魔，\r\n                                <br>\r\n                                　　这里的兴趣爱好千奇百怪。\r\n                                <br>\r\n                                　　泛舟学海，攀登书山。\r\n                                <br>\r\n                                　　这个一脸茫然的年轻人跌跌撞撞的开始了自己的巫师生涯。        </p>', '3574884');
INSERT INTO `novel` VALUES ('16', '超凡大卫', '吃瓜子群众', '“影侍，你看看这些都是什么？”“一堆入门级知识光球，我是垃圾堆吗？”大卫对着身边漂浮的影侍责怪道。影侍脸涨的通红，向主人展示自己非常努力的在工作。无垠的星空，神的侍者，文明的碰撞，无敌的战舰，恐怖的虫族，超凡的生命，卑微的凡人，开启的新纪元。', '魔兽践踏，巨龙咆哮，巫师诅咒，魔法璀璨之光照耀知识灯塔！', 'images/info-img-16.jpg', '5', '奇幻', null, '第一章 新生', '<p>\r\n                                　　“影侍，你看看这些都是什么？”\r\n                                <br>\r\n                                　　“一堆入门级知识光球，我是垃圾堆吗？”大卫对着身边漂浮的影侍责怪道。影侍脸涨的通红，向主人展示自己非常努力的在工作。\r\n                                <br>\r\n                                　　无垠的星空，神的侍者，文明的碰撞，无敌的战舰，恐怖的虫族，超凡的生命，卑微的凡人，开启的新纪元。 </p>', '2185485');
INSERT INTO `novel` VALUES ('17', '盖世双谐', '三天两觉', '江湖路上走走停停翻开年少漂泊的回忆如今走过这世间，万般留恋峰吹起了从前', '借三尺明月，衔两袖青龙，轻剑快马恣意，携侣江湖同游！', 'images/info-img-17.jpg', '5', '武侠', null, '楔子', '<p>\r\n                                　　江湖路上走走停停\r\n                                <br>\r\n                                　　翻开年少漂泊的回忆\r\n                                <br>\r\n                                　　如今走过这世间，万般留恋\r\n                                <br>\r\n                                　　峰吹起了从前      </p>', '1856450');
INSERT INTO `novel` VALUES ('18', '大数据修仙', '陈风笑', '冯君身为985的双学位，毕业就失业了，在都市中艰难打拼，偏偏还放不下架子，他的处境可想而知。直到有一天，他和手机被雷劈了，猛然发现，原来自己可以化身数据，进入app中。化身数据进入app中能做什么？能做的太多了，尽情想像吧……慢着，手机银行的存款？住手，那个不能乱改！当然，这并不仅仅是全部，然后他还发现，自己居然可以自由进入仙侠位面……让我们跟随冯君，踏上奇妙的修仙之旅，“落花时节又逢君”，这话可不是白说的……', '修仙觅长生，热血任逍遥，踏莲曳波涤剑骨，凭虚御风塑圣魂！', 'images/info-img-18.jpg', '5', '仙侠', null, '第一章 谁在渡劫', '<p>\r\n                                　　冯君身为985的双学位，毕业就失业了，在都市中艰难打拼，偏偏还放不下架子，他的处境可想而知。\r\n                                <br>\r\n                                　　直到有一天，他和手机被雷劈了，猛然发现，原来自己可以化身数据，进入app中。\r\n                                <br>\r\n                                　　化身数据进入app中能做什么？能做的太多了，尽情想像吧……慢着，手机银行的存款？住手，那个不能乱改！\r\n                                <br>\r\n                                　　当然，这并不仅仅是全部，然后他还发现，自己居然可以自由进入仙侠位面……\r\n                                <br>\r\n                                　　让我们跟随冯君，踏上奇妙的修仙之旅，“落花时节又逢君”，这话可不是白说的…… </p>', '1286548');
INSERT INTO `novel` VALUES ('19', '我的1979', '争斤论两花花帽', '一觉醒来，回到那个年代，再次面对过往，你猜不透的结局..........', '重生1979，文字很赞，让人仿佛回到了那个充满变革的年代', 'images/info-img-19.jpg', '5', '现实', null, '第一章 一重浪灭一重生', '<p>\r\n                                　　一觉醒来，回到那个年代，再次面对过往，你猜不透的结局..........</p>', '1124154');
INSERT INTO `novel` VALUES ('20', '从我是特种兵开始打卡', '纳兰凌云', '穿越到《我是特种兵》的世界，得到辅助引擎的支持，他逐步成为所有士兵的王，特种兵，通信兵，飞行员等等，在每一个兵种的领域他都做到了极致，成为当之无愧所有士兵的王！', '热血战斗，保家卫国，誓死守护，傲骨无双铸军魂！', 'images/info-img-20.jpg', '5', '军事', null, '第一章 咸鱼', '<p>\r\n                                　　穿越到《我是特种兵》的世界，得到辅助引擎的支持，他逐步成为所有士兵的王，特种兵，通信兵，飞行员等等，在每一个兵种的领域他都做到了极致，成为当之无愧所有士兵的王！\r\n</p>', '1108544');
INSERT INTO `novel` VALUES ('21', '小阁老', '三戒大师', '站在你面前的是：大明王朝的守护者，万历皇帝的亲密战友，内阁首辅的好儿子，十六、十七世纪全球首富。控制吏部三十年的幕后黑手，宗藩制度的掘墓人，东林党口中的严世藩第二，张居正高呼不可战胜。海瑞的知己，徐渭的东家，利玛窦的剃度人，徐光启等六位状元的授业恩师。大明诗坛遮羞布，七百余种各学科书籍撰写者，两千七百余项专利的发明人，现代大学与科学的奠基者。海外汉人的保护神，新航路的开辟者，大洋秩序的维持者，全球大型工程的承包商。祸乱欧洲的罪魁祸首，德川家康的义父，塞巴斯蒂安的拯救者，一心为民的小阁老。', '醒掌天下权，醉卧美人膝，五千年风华烟雨，是非成败转头空！', 'images/info-img-21.jpg', '5', '历史', null, '第一章 我来享福了', '<p>\r\n                                　　大明王朝的守护者，万历皇帝的亲密战友，内阁首辅的好儿子，十六、十七世纪全球首富。\r\n                                <br>\r\n                                　　控制吏部三十年的幕后黑手，宗藩制度的掘墓人，东林党口中的严世藩第二，张居正高呼不可战胜。\r\n                                <br>\r\n                                　　海瑞的知己，徐渭的东家，利玛窦的剃度人，徐光启等六位状元的授业恩师。\r\n                                <br>\r\n                                　　大明诗坛遮羞布，七百余种各学科书籍撰写者，两千七百余项专利的发明人，现代大学与科学的奠基者。\r\n                                <br>\r\n                                　　海外汉人的保护神，新航路的开辟者，大洋秩序的维持者，全球大型工程的承包商。\r\n                                <br>\r\n                                　　祸乱欧洲的罪魁祸首，德川家康的义父，塞巴斯蒂安的拯救者，一心为民的小阁老。 </p>', '1085451');
INSERT INTO `novel` VALUES ('22', '峡谷之巅', '神秘的大西瓜', '从小被教育做人做事要稳健，谋而后动，三思而后行，准备充分再做的陈稳，接受了这种教育，却在游戏里对稳健有着特别的理解。发育好才能赢，那让对面发育不好，我岂不是就是双倍的发育？再把对面杀了，不就是四倍的发育了吗？哎，打野怎么来了？一个逻辑鬼才的封神之路！', '肆意挥洒激情的游戏人生，打破现实框架的无尽幻想！', 'images/info-img-22.jpg', '5', '游戏', null, '第一章 稳健的欧皇', '<p>\r\n                                　　从小被教育做人做事要稳健，谋而后动，三思而后行，准备充分再做的陈稳，接受了这种教育，却在游戏里对稳健有着特别的理解。\r\n                                <br>\r\n                                　　发育好才能赢，那让对面发育不好，我岂不是就是双倍的发育？\r\n                                <br>\r\n                                　　再把对面杀了，不就是四倍的发育了吗？\r\n                                <br>\r\n                                　　哎，打野怎么来了？\r\n                                <br>\r\n                                　　一个逻辑鬼才的封神之路！\r\n</p>', '984545');
INSERT INTO `novel` VALUES ('23', '是篮球之神啊', '快剑江湖', '2006年春天，斯坦福即将被裁掉的替补后卫李真在雷电中醒来。一个声音在脑袋里响起：“这个世界有太多篮球运动员挥霍上天赐予的天赋，只要你足够努力，我会惩恶扬善，帮你攫取他们的天赋”“凡是为伤害你而做的动作都将被惩处，你会得到施暴者的天赋。”“还有…如果有人毫无保留的爱你，你也会提升……”“你才是真正的天选之子。”“你会是超越那个老流氓的篮球之神。”', '体坛多英雄，赛场我为王，热血澎湃的竞技体育！', 'images/info-img-23.jpg', '5', '体育', null, '第一章 【如果一切重来】', '<p>\r\n                                　　2006年春天，斯坦福即将被裁掉的替补后卫李真在雷电中醒来。\r\n                                <br>\r\n                                　　一个声音在脑袋里响起：“这个世界有太多篮球运动员挥霍上天赐予的天赋，只要你足够努力，我会惩恶扬善，帮你攫取他们的天赋”\r\n                                <br>\r\n                                　　“凡是为伤害你而做的动作都将被惩处，你会得到施暴者的天赋。”\r\n                                <br>\r\n                                　　“还有…如果有人毫无保留的爱你，你也会提升……”\r\n                                <br>\r\n                                　　“你才是真正的天选之子。”\r\n                                <br>\r\n                                　　“你会是超越那个老流氓的篮球之神。”</p>', '984212');
INSERT INTO `novel` VALUES ('24', '诸天尽头', '凤嘲凰', '一觉醒来，罗素脑海中就多出了一个可以穿梭诸天世界的系统。杀手、怪兽、女巫、异形、魔鬼、神明……无限的世界有无限的可能，亦有无限的历险，而踏破无限之人，必将留下属于自己的传说。从平凡到不朽，在诸天的道路上寻觅尽头！……看着永无止尽的道路，罗素摸着系统的狗头，他手里只有抽奖得来的卡片……有点慌！', '星海漫游，时空穿梭，机械科技，目标是未知的星辰大海！', 'images/info-img-24.jpg', '5', '科幻', null, '第一章 生活远比小说更扯淡', '<p>\r\n                                　　一觉醒来，罗素脑海中就多出了一个可以穿梭诸天世界的系统。\r\n                                <br>\r\n                                　　杀手、怪兽、女巫、异形、魔鬼、神明……\r\n                                <br>\r\n                                　　无限的世界有无限的可能，亦有无限的历险，而踏破无限之人，必将留下属于自己的传说。\r\n                                <br>\r\n                                　　从平凡到不朽，在诸天的道路上寻觅尽头！\r\n                                <br>\r\n                                　　……\r\n                                <br>\r\n                                　　看着永无止尽的道路，罗素摸着系统的狗头，他手里只有抽奖得来的卡片……有点慌！    </p>', '842121');
INSERT INTO `novel` VALUES ('25', '诡异生存游戏', '大肥鱼本尊', '一封神秘短信，让徐阳卷入诡异事件当中。真实存在，就在生活周围，直面恐怖，只有找到生路的人，才可以活下去。', '考古探险、鉴宝收藏，侦探推理、诡秘分析，戏说传承古今中外的民间悬疑文化！', 'images/info-img-25.jpg', '5', '悬疑', null, '第1章 开端', '<p>\r\n                                　　一封神秘短信，让徐阳卷入诡异事件当中。\r\n                                <br>\r\n                                　　真实存在，就在生活周围，直面恐怖，只有找到生路的人，才可以活下去。</p>', '753165');
INSERT INTO `novel` VALUES ('26', '奶爸的异界餐厅', '轻语江湖', '诺兰大陆的混乱之城中，有着一家奇怪的餐厅。在这里，精灵要和矮人拼桌，兽人被严禁喧哗，巨龙只能围坐在餐厅前的小广场上，恶魔甚至需要自己带特制的凳子……但就是这么一家规矩奇葩的餐厅，门口却每天都排着长队。精灵们不顾仪态的撸串，巨龙们握着漏勺围坐在火锅前，恶魔们吃着可爱的团子……', '热血的少年，为打破次元壁一往无前！', 'images/info-img-26.jpg', '5', '轻小说', null, '第一章 我家的女孩', '<p>\r\n                                　　诺兰大陆的混乱之城中，有着一家奇怪的餐厅。\r\n                                <br>\r\n                                　　在这里，精灵要和矮人拼桌，兽人被严禁喧哗，巨龙只能围坐在餐厅前的小广场上，恶魔甚至需要自己带特制的凳子……\r\n                                <br>\r\n                                　　但就是这么一家规矩奇葩的餐厅，门口却每天都排着长队。\r\n                                <br>\r\n                                　　精灵们不顾仪态的撸串，巨龙们握着漏勺围坐在火锅前，恶魔们吃着可爱的团子……\r\n                                <br>\r\n                                　　“这里的美食在大陆上找不到第二家！这个老板是个天才！”有客人这样评价，然后偷偷看了一眼门口：“还有，千万别想着抓走老板或者吃霸王餐，不然你会死的很惨。”\r\n                                <br>\r\n                                　　“吃饭，给钱，不然通通打死。”一个小萝莉在门口踱着脚步，奶声奶气的说道，一旁五六米高的巨龙不禁打了个寒颤。        </p>', '654512');
INSERT INTO `novel` VALUES ('27', '超神机械师', '齐佩甲', '韩萧，《星海》骨灰级代练，被来自东（zuo）方(zhe)的神秘力量扔进穿越大军，携带玩家面板变成NPC，回到《星海》公测之前，毅然选择难度最高的机械系。战舰列队纵横星海，星辰机甲夭矫如龙，幽能炮毁天灭地，还有无边无际的机械大军，静静待在随身仓库里。一人，即是军团！如果不是玩家出现，本书就是正经严肃的穿越异界题材……作为NPC，正常NPC对玩家功能一应俱全……发布任务？好感度调节？传授技能？哎等等，这群玩家我怎么都认识？得，现实世界也回到了十年前。', '肆意挥洒激情的游戏人生，打破现实框架的无尽幻想！', 'images/info-img-27.jpg', '5', '游戏', null, '001 初生', '<p>\r\n                                　　韩萧，《星海》骨灰级代练，被来自东（zuo）方(zhe)的神秘力量扔进穿越大军，携带玩家面板变成NPC，回到《星海》公测之前，毅然选择难度最高的机械系\r\n                                <br>\r\n                                　　战舰列队纵横星海，星辰机甲夭矫如龙，幽能炮毁天灭地，还有无边无际的机械大军，静静待在随身仓库里。\r\n                                <br>\r\n                                　　一人，即是军团！\r\n                                <br>\r\n                                　　如果不是玩家出现，本书就是正经严肃的穿越异界题材……\r\n                                <br>\r\n                                　　作为NPC，正常NPC对玩家功能一应俱全……发布任务？好感度调节？传授技能？\r\n                                <br>\r\n                                　　哎等等，这群玩家我怎么都认识？\r\n                                <br>\r\n                                　　得，现实世界也回到了十年前。\r\n                            </p>', '454123');

-- ----------------------------
-- Table structure for user
-- ----------------------------
DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `id` int NOT NULL AUTO_INCREMENT,
  `uid` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL,
  `username` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL,
  `password` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

